import { reasons } from '../components/reasons';

import {
  HelpBlock,
  Form,
  FormControl,
  FormGroup,
  IconButton,
  Icon,
  FlexboxGrid,
  Checkbox,
  InputPicker,
  CheckboxGroup,
  Schema,
  Radio,
  RadioGroup,
  InputGroup,
  Input,
} from 'rsuite';
import { useHistory } from 'react-router';

const Newv = ({ activeOrder, orderNumber, postReturn }) => {
