import React, { useState, UseState } from 'react';
import ItemCard from '../components/ItemCard';
import { connect } from 'react-redux';
import {
  fetchMyOrder,
  fetch,
  PostReturnData,
  PostReturnReasons,
} from '../Actions';
import { reasons } from '../components/reasons';

import {
  HelpBlock,
  Form,
  FormControl,
  FormGroup,
  IconButton,
  Icon,
  FlexboxGrid,
  Checkbox,
  InputPicker,
  CheckboxGroup,
  Schema,
  Radio,
  RadioGroup,
  Input,
} from 'rsuite';
import { useHistory } from 'react-router';

const ItemSelect = ({ activeOrder, orderNumber, postReturn, postReasons }) => {
  const { ArrayType } = Schema.Types;
  const model = Schema.Model({
    ReturnItems: ArrayType(),
  });

  const [returnOrder, setReturnOrder] = useState();
  const [returnitems, setReturnitems] = useState([]);

  const show = activeOrder;

  console.log(returnOrder);
  console.log(returnitems);

  const handleSubmit = () => {
    console.log('yoo');
  };

  const history = useHistory();

  const handleNext = () => {
    //  postReturn(returnOrder);
    postReasons(returnitems);

    // history.push('./printlabel');
  };

  const handleBack = () => {
    history.goBack();
  };

  return (
    <div>
      <FlexboxGrid justify='center'>
        <FlexboxGrid.Item colspan={18}>
          <h1>Returns</h1>
        </FlexboxGrid.Item>
        <FlexboxGrid.Item colspan={18}>
          <h4>Order Number: {orderNumber}</h4>
        </FlexboxGrid.Item>
        <FlexboxGrid.Item colspan={20}>
          <Form
            model={model}
            fluid
            onChange={(formValue) => {
              console.log(formValue);
              setReturnOrder(formValue);
            }}
          >
            <FormGroup name='items'>
              <FlexboxGrid justify='space-around' align='top'>
                <FlexboxGrid.Item>
                  {show &&
                    show.map((items, index) => {
                      return (
                        <div key={items.itemid}>
                          <ItemCard items={items} />
                          <InputPicker
                            data={reasons}
                            onChange={(value) => {
                              let newArr = [...returnitems];
                              newArr[index] = {
                                reason: value,
                                item: items.itemid,
                                itemName: items.itemname,
                              };
                              setReturnitems(newArr);
                            }}
                          />
                        </div>
                      );
                    })}
                </FlexboxGrid.Item>
              </FlexboxGrid>
            </FormGroup>

            <FormGroup name='info'>
              <FormControl name='email' placeholder='Email' />
              <HelpBlock tooltip>
                This is where will will send the return label
              </HelpBlock>
            </FormGroup>

            <FormGroup>
              <FormControl name='name' placeholder='Name' />
              <HelpBlock tooltip>This field is required</HelpBlock>
            </FormGroup>

            <FormGroup>
              <IconButton
                onClick={handleNext}
                icon={<Icon icon='arrow-right' />}
              >
                Next
              </IconButton>
            </FormGroup>
          </Form>
        </FlexboxGrid.Item>
      </FlexboxGrid>
      );
    </div>
  );
};

const mapStateToProps = (state) => ({
  activeOrder: state.activeOrder.basket,
  orderNumber: state.activeOrder.userOrder,
});

const mapDispatchToProps = (dispatch) => {
  return {
    postReturn: (returnOrder) => dispatch(PostReturnData(returnOrder)),
    postReasons: (returnItems) => dispatch(PostReturnReasons(returnItems)),
  };
};

export default connect(mapStateToProps, mapDispatchToProps)(ItemSelect);
