import { MY_ORDER, NOT_FOUND, FIND_MY_ORDER } from '../Actions/types';

const initialState = {
  userOrder: null,
  basket: [],
  loading: false,
};

export const userOrder = (state = initialState, { type, payload }) => {
  switch (type) {
    case FIND_MY_ORDER:
      return {
        ...state,
        loading: false,
        userOrder: null,
      };
    case MY_ORDER:
      return {
        ...state,
        userOrder: payload.orderNum,
        basket: payload.basket,
        loading: false,
      };
    case NOT_FOUND:
      return {
        ...state,
        loading: false,
        userOrder: null,
      };

    default:
      return state;
  }
};
