import React, { Component } from 'react';
import ItemCard from '../components/ItemCard';
import {
  HelpBlock,
  Form,
  FormControl,
  FormGroup,
  IconButton,
  Icon,
  FlexboxGrid,
  Button,
} from 'rsuite';

export class ItemSelect extends Component {
  render() {
    const data = [
      {
        image: 'Shocks',
        size: 7,
        id: 30494,
      },
      {
        image: 'Shocks',
        size: 4,
        id: 4342432,
      },
      {
        image: 'Louisa',
        size: 6,
        id: 32323,
      },
    ];

    const showing = false;

    return (
      <FlexboxGrid justify='center'>
        <FlexboxGrid.Item colspan={18}>
          <h1>Returns</h1>
        </FlexboxGrid.Item>
        <FlexboxGrid.Item colspan={18}>
          <h4>Order Number:</h4>
        </FlexboxGrid.Item>
        <FlexboxGrid.Item colspan={20}>
          <Form fluid>
            <FormGroup>
              {data.map((items) => {
                return <ItemCard items={items} key={items.id} />;
              })}
            </FormGroup>

            <FormGroup>
              <FormControl name='email' placeholder='Email' />
              <HelpBlock tooltip>
                This is where will will send the return label
              </HelpBlock>
            </FormGroup>

            <FormGroup>
              <FormControl name='name' placeholder='Name' />
              <HelpBlock tooltip>This field is required</HelpBlock>
            </FormGroup>

            <FormGroup>
              <IconButton icon={<Icon icon='arrow-right' />}>Next</IconButton>
              <Button disabled={showing ? false : true}></Button>
            </FormGroup>
          </Form>
        </FlexboxGrid.Item>
      </FlexboxGrid>
    );
  }
}

export default ItemSelect;
