import React, { useEffect, useState } from 'react';
import ItemCard from '../components/ItemCard';
import { connect } from 'react-redux';
import { fetchMyOrder, fetch } from '../Actions';
import { reasons } from '../components/reasons';

import {
  HelpBlock,
  Form,
  FormControl,
  FormGroup,
  IconButton,
  Icon,
  FlexboxGrid,
  Checkbox,
  InputPicker,
} from 'rsuite';

const ItemSelect = ({ activeOrder }) => {
  const showing = true;

  const [Returning, setReturing] = useState(true);
  const [returnBasket, setReturnBasket] = useState([]);

  const formValue = {
    email: '',
    name: '',
    returnId: 'Chrome',
    selectReason: ['open'],
  };

  useEffect(() => {
    fetchMyOrder();
  }, []);

  const handleSubmit = () => {
    console.log('yoo');
  };

  const handleSw = (value) => {
    console.log(value);
  };

  console.log(setReturnBasket);

  return (
    <FlexboxGrid justify='center'>
      <FlexboxGrid.Item colspan={18}>
        <h1>Returns</h1>
      </FlexboxGrid.Item>
      <FlexboxGrid.Item colspan={18}>
        <h4>Order Number:</h4>
      </FlexboxGrid.Item>
      <FlexboxGrid.Item colspan={20}>
        <Form
          fluid
          onChange={(formValue) => {
            console.log({ formValue });
          }}
        >
          <FormGroup>
            {activeOrder &&
              activeOrder.map((items) => {
                return (
                  <div>
                    <FormControl accepter={Checkbox} />
                    <FlexboxGrid justify='space-around' align='bottom'>
                      <FlexboxGrid.Item colspan={18}>
                        <ItemCard items={items} key={items.id} />
                      </FlexboxGrid.Item>
                      <FlexboxGrid.Item colspan={6}>
                        <FormControl
                          accepter={Checkbox}
                          value={items.id}
                          name='returnIndicator'
                        >
                          Return
                        </FormControl>

                        {!Returning ? null : (
                          <FormControl
                            accepter={InputPicker}
                            data={reasons}
                            placeholder='Select Reason'
                            size='md'
                            onChange={(value) => console.log(value)}
                            name='selectReason'
                          />
                        )}
                      </FlexboxGrid.Item>
                    </FlexboxGrid>
                  </div>
                );
              })}
          </FormGroup>

          <FormGroup>
            <FormControl accepter={Checkbox} name='selector' />
          </FormGroup>

          <FormGroup>
            <FormControl name='email' placeholder='Email' />
            <HelpBlock tooltip>
              This is where will will send the return label
            </HelpBlock>
          </FormGroup>

          <FormGroup>
            <FormControl name='name' placeholder='Name' />
            <HelpBlock tooltip>This field is required</HelpBlock>
          </FormGroup>

          <FormGroup>
            <IconButton
              onClick={handleSubmit}
              icon={<Icon icon='arrow-right' />}
              disabled={showing ? false : true}
            >
              Next
            </IconButton>
          </FormGroup>
        </Form>
      </FlexboxGrid.Item>
    </FlexboxGrid>
  );
};
const mapStateToProps = (state) => ({
  activeOrder: state.activeOrder,
});

export default connect(mapStateToProps)(ItemSelect);
