import React, { useState, UseState } from 'react';
import ItemCard from '../components/ItemCard';
import { connect } from 'react-redux';
import {
  fetchMyOrder,
  fetch,
  PostReturnData,
  PostReturnReasons,
} from '../Actions';
import { reasons } from '../components/reasons';

import {
  HelpBlock,
  Form,
  FormControl,
  FormGroup,
  IconButton,
  Icon,
  FlexboxGrid,
  Checkbox,
  InputPicker,
  CheckboxGroup,
  Schema,
  Radio,
  RadioGroup,
  Input,
  Button,
} from 'rsuite';
import { useHistory, useParams } from 'react-router';

const ItemSelect = ({
  activeOrder,
  orderNumber,
  postReturn,
  postReasons,
  returnData,
}) => {
  const { ArrayType, StringType, NumberType } = Schema.Types;
  const model = Schema.Model({
    name: StringType()
      .minLength(2, 'Please select at least 2 types of Skills.')
      .isRequired('This field is required.'),
    status: ArrayType()
      .minLength(2, 'Please select at least 2 types of Status.')
      .isRequired('This field is required.'),
    level: NumberType().min(5, 'This field must be greater than 5'),
  });

  const { order } = useParams();

  const [returnOrder, setReturnOrder] = useState();
  const [returnitems, setReturnitems] = useState([]);

  const show = activeOrder;

  const returnInfo = {};

  returnInfo.name = returnOrder ? returnOrder.name : null;
  returnInfo.items = returnitems;
  returnInfo.contact = returnOrder ? returnOrder.email : null;

  const history = useHistory();

  const handleConfirm = () => {
    postReturn(returnInfo);
  };

  const handleNext = () => {
    //  postReturn(returnOrder);
  };

  const handleBack = () => {
    history.goBack();
  };

  console.log(returnInfo);

  return (
    <div>
      <FlexboxGrid justify='center'>
        <FlexboxGrid.Item colspan={18}>
          <h1>Returns</h1>
        </FlexboxGrid.Item>
        <FlexboxGrid.Item colspan={18}>
          <h4>Order Number: {orderNumber}</h4>
        </FlexboxGrid.Item>
        <FlexboxGrid.Item colspan={20}>
          <Form
            fluid
            onChange={(formValue) => {
              console.log(formValue);
              setReturnOrder(formValue);
            }}
          >
            <FormGroup name='items'>
              <FlexboxGrid justify='space-around' align='top'>
                <FlexboxGrid.Item>
                  {show &&
                    show.map((items, index) => {
                      return (
                        <div key={items.itemid}>
                          <ItemCard items={items} />
                          <InputPicker
                            data={reasons}
                            onChange={(value) => {
                              let newArr = [...returnitems];
                              newArr[index] = {
                                reason: value,
                                item: items.itemid,
                                itemName: items.itemname,
                              };
                              setReturnitems(newArr);
                            }}
                          />
                        </div>
                      );
                    })}
                </FlexboxGrid.Item>
              </FlexboxGrid>
            </FormGroup>

            <FormGroup name='info'>
              <FormControl
                name='email'
                placeholder='Email'
                plaintextDefaultValue='insert name'
              />
              <HelpBlock tooltip>
                This is how we will contact you if needed
              </HelpBlock>
            </FormGroup>

            <FormGroup>
              <FormControl name='name' placeholder='Name' />
              <HelpBlock tooltip>This field is required</HelpBlock>
            </FormGroup>

            <FormGroup>
              <FlexboxGrid justify='space-around'>
                <Button
                  onClick={handleConfirm}
                  style={{ margin: 7 }}
                  disabled={!returnInfo.name || !returnInfo.contact}
                >
                  Submit
                </Button>

                {returnData ? (
                  <IconButton
                    style={{ margin: 7 }}
                    onClick={handleNext}
                    icon={<Icon icon='arrow-right' />}
                  >
                    Next
                  </IconButton>
                ) : null}
              </FlexboxGrid>
            </FormGroup>
          </Form>
        </FlexboxGrid.Item>
      </FlexboxGrid>
      );
    </div>
  );
};

const mapStateToProps = (state) => ({
  // returnData: state.activeReturn.retunNo,
  activeOrder: state.activeOrder.basket,
  returnData: state.returnOrder.returnNo,
});

const mapDispatchToProps = (dispatch) => {
  return {
    postReturn: (returnOrder) => dispatch(PostReturnData(returnOrder)),
    postReasons: (returnItems) => dispatch(PostReturnReasons(returnItems)),
  };
};

export default connect(mapStateToProps, mapDispatchToProps)(ItemSelect);
