import React, { useState } from 'react';
import {
  Panel,
  FlexboxGrid,
  FormControl,
  Radio,
  RadioGroup,
  Cascader,
} from 'rsuite';
import FlexboxGridItem from 'rsuite/lib/FlexboxGrid/FlexboxGridItem';
import { reasons } from './reasons';
function ItemCard({ items, callbackChoice }) {
  const [returnReason, setReturnReason] = useState([]);
  console.log(returnReason);
  return (
    <FlexboxGrid align='middle'>
      <FlexboxGrid.Item colspan={8}>
        <img
          src='https://i.pinimg.com/474x/e6/3d/69/e63d6960e27825cc4a32d5b984de2994.jpg'
          height='140'
          alt='juup'
        />
        <Panel header={items.itemname} style={{ height: 140 }}>
          <p>
            <small>Size: {items.size} </small>
          </p>
          <p>
            <small>Size: </small>
          </p>
        </Panel>
      </FlexboxGrid.Item>
      <FlexboxGridItem colspan={16}>
        <FormControl
          accepter={RadioGroup}
          name={reasons}
          inline
          onChange={(value) => {
            returnReason.concat(value);
          }}
        >
          <Radio value='Other'>Other</Radio>
          <Radio value='Size'>Size</Radio>
          <Radio value='colors'>colors</Radio>
          <Radio value='Style'>Style</Radio>
        </FormControl>
      </FlexboxGridItem>
    </FlexboxGrid>
  );
}

export default ItemCard;
