import React, { useState, UseState } from 'react';
import ItemCard from '../components/ItemCard';
import { connect } from 'react-redux';
import { fetchMyOrder, fetch, PostReturnData } from '../Actions';
import { reasons } from '../components/reasons';

import {
  HelpBlock,
  Form,
  FormControl,
  FormGroup,
  IconButton,
  Icon,
  FlexboxGrid,
  Checkbox,
  InputPicker,
  CheckboxGroup,
  Schema,
  Radio,
  RadioGroup,
  InputGroup,
  Input,
} from 'rsuite';
import { useHistory } from 'react-router';
import FlexboxGridItem from 'rsuite/lib/FlexboxGrid/FlexboxGridItem';

const Newv = ({ activeOrder, orderNumber, postReturn }) => {
  const { ArrayType } = Schema.Types;
  const model = Schema.Model({
    ReturnItems: ArrayType(),
  });

  const [returnEmail, setReturnEmail] = useState();
  const [returnitems, setReturnitems] = useState();
  const [returnName, setReturnName] = useState();
  const [returnReason, setReturnReason] = useState([]);

  const show = activeOrder;

  console.log(returnEmail);
  console.log(returnitems);
  console.log(returnName);
  console.log(returnReason);

  const handleSubmit = () => {
    console.log('yoo');
  };

  const history = useHistory();

  const handleNext = () => {
    history.push('./printlabel');
  };

  const handleBack = () => {
    history.goBack();
  };

  const updateFieldChanged = (index) => (e) => {
    console.log('index: ' + index);
    console.log('property name: ' + e.target.name);
    let newArr = [...returnReason]; // copying the old datas array
    newArr[index] = e.target.value; // replace e.target.value with whatever you want to change it to

    setReturnReason(newArr); // ??
  };

  return (
    <div>
      <FlexboxGrid justify='center'>
        <FlexboxGrid.Item colspan={18}>
          <h1>Returns</h1>
        </FlexboxGrid.Item>
        <FlexboxGrid.Item colspan={18}>
          <h4>Order Number: {orderNumber}</h4>
        </FlexboxGrid.Item>

        <FlexboxGrid.Item colspan={20}>
          <InputGroup>
            <InputGroup.Addon>
              <Icon icon='avatar' />
            </InputGroup.Addon>
            <Input
              onChange={(value) => {
                setReturnName(value);
              }}
            />
          </InputGroup>
        </FlexboxGrid.Item>
        <FlexboxGridItem colspan={18}>
          <CheckboxGroup
            onChange={(value) => {
              setReturnitems(value);
            }}
            name='ReturnItems'
            inline
          >
            {show &&
              show.map((items, index) => {
                return (
                  <div key={items.size}>
                    <Checkbox value={items}>return</Checkbox>
                    <ItemCard items={items} />
                    <Form onChange={updateFieldChanged(index)}>
                      <FormControl
                        accepter={InputPicker}
                        data={reasons}
                        name={items.itemid}
                      />
                    </Form>
                  </div>
                );
              })}
          </CheckboxGroup>
        </FlexboxGridItem>

        <FlexboxGrid.Item colspan={20}>
          <InputGroup>
            <InputGroup.Addon>
              <Icon icon='avatar' />
            </InputGroup.Addon>
            <Input
              onChange={(value) => {
                setReturnEmail(value);
              }}
            />
          </InputGroup>
        </FlexboxGrid.Item>
      </FlexboxGrid>
    </div>
  );
};

const mapStateToProps = (state) => ({
  activeOrder: state.activeOrder.basket,
  orderNumber: state.activeOrder.userOrder,
});

const mapDispatchToProps = (dispatch) => {
  return {
    postReturn: (returnItems) => dispatch(PostReturnData(returnItems)),
  };
};

export default connect(mapStateToProps, mapDispatchToProps)(Newv);
