import axios from 'axios';
import generator from 'generate-serial-number';

export const FetchOrderData = (orders) => {
  return async (dispatch) => {
    console.log(orders);
    dispatch({
      type: 'FIND_MY_ORDER',
    });
    const itemList = await axios.post(
      'http://localhost:3000/fetchOrderedItems',
      {
        orderNum: orders,
      }
    );
    console.log(itemList);

    if (itemList.data !== 'not found') {
      dispatch(OrderData(itemList.data));
    } else {
      dispatch({
        type: 'NOT_FOUND',
      });
    }
  };
};

export const PostReturnData = (data) => {
  return async (dispatch) => {
    data.returnNo = generator.generate(12);

    console.log(data);

    const move = await axios.post('http://localhost:3000/fetchOrderedItems', {
      returnData: data,
    });

    dispatch({
      type: 'RETURNING_ITEMS',
      payload: data,
    });
  };
};

export const PostReturnReasons = (reasons) => {
  return async (dispatch) => {
    console.log(reasons);
    const plachldr = await axios.post(
      'http://localhost:3000/postReturnReasons',
      {
        returnReasons: reasons,
      }
    );

    dispatch({
      type: 'RETURNING_REASONS',
      payload: reasons,
    });
  };
};

export const NewReturn = () => ({
  type: 'NEW_RETURN',
});

export const fetch = async () => {
  await axios.get('./RefundReasons.JSON').then((res) => console.log(res.data));
};

export const UserOrderSelection = (userOrderNum) => ({
  type: 'UPDATE_ORDER_NUMBER',
  payload: userOrderNum,
});

export const UpdateReturn = (returningItemsArray) => ({
  type: 'RETURNING_ITEMS',
  payload: returningItemsArray,
});

export const OrderData = (orders) => ({
  type: 'MY_ORDER',
  payload: orders,
});
