import axios from 'axios';
import React, { useState, useEffect } from 'react';
import { connect } from 'react-redux';
import { UserOrderSelection } from '../Actions';
import {
  Input,
  Whisper,
  Tooltip,
  Button,
  HelpBlock,
  FormControl,
  ControlLabel,
  FormGroup,
  FlexboxGrid,
  Form,
  Panel,
} from 'rsuite';

import 'rsuite/dist/styles/rsuite-default.css';

const Returns = ({ UserOrder }) => {
  const [isLoading, setLoading] = useState(false);
  const [userOrderNumber, setuserOrderNumer] = useState();

  useEffect(() => {
    axios.get('./RefundReasons.JSON').then((res) => {
      return res.data;
    });
  }, []);

  const handleSubmit = () => {
    UserOrder(userOrderNumber);
    setuserOrderNumer('');
  };

  return (
    <div>
      <FlexboxGrid justify='center' align='middle' style={{ height: 600 }}>
        <FlexboxGrid.Item colspan={18}>
          <Panel header={<h2>returns</h2>} bordered>
            <Form
              onChange={(formValue) => {
                setuserOrderNumer({ formValue });
              }}
              fluid
            >
              <FormGroup>
                <ControlLabel>Order number</ControlLabel>
                <FormControl name='orderNumber' />
                <HelpBlock tooltip>Required</HelpBlock>
              </FormGroup>

              <Button onClick={handleSubmit} loading={isLoading ? true : false}>
                Submit
              </Button>
            </Form>
          </Panel>
        </FlexboxGrid.Item>
      </FlexboxGrid>
    </div>
  );
};

<Whisper trigger='focus' speaker={<Tooltip>Enter your order number</Tooltip>}>
  <Input style={{ width: 300 }} placeholder='AA00000A0000' size='lg' />
</Whisper>;

const mapDispatchToProps = (dispatch) => {
  return {
    UserOrder: (userOrderNum) => dispatch(UserOrderSelection(userOrderNum)),
  };
};

export default connect(null, mapDispatchToProps)(Returns);
