import React, { Component } from 'react';
import ItemCard from '../components/ItemCard';
import {
  HelpBlock,
  Form,
  FormControl,
  FormGroup,
  IconButton,
  Icon,
  FlexboxGrid,
} from 'rsuite';

export class ItemSelect extends Component {
  render() {
    const data = [
      {
        image: 'Eugenia',
        size: 'Eugenia',
        id: 'Master',
      },
      {
        image: 'Kariane',
        size: 'Kariane',
        id: 'Master',
      },
      {
        image: 'Louisa',
        size: 'Louisa',
        id: 'Master',
      },
    ];

    return (
      <FlexboxGrid justify='center'>
        <FlexboxGrid.Item colspan={18}>
          <h1>Returns</h1>
        </FlexboxGrid.Item>
        <FlexboxGrid.Item colspan={18}>
          <h4>Order Number:</h4>
        </FlexboxGrid.Item>
        <FlexboxGrid.Item colspan={20}>
          <Form fluid>
            <FormGroup>
              {data.map((items) => {
                return <ItemCard />;
              })}
              <ItemCard />
            </FormGroup>

            <FormGroup>
              <FormControl name='email' placeholder='Email' />
              <HelpBlock tooltip>
                This is where will will send the return label
              </HelpBlock>
            </FormGroup>

            <FormGroup>
              <FormControl name='name' placeholder='Name' />
              <HelpBlock tooltip>This field is required</HelpBlock>
            </FormGroup>

            <FormGroup>
              <IconButton icon={<Icon icon='arrow-right' />} placement='right'>
                Next
              </IconButton>
            </FormGroup>
          </Form>
        </FlexboxGrid.Item>
      </FlexboxGrid>
    );
  }
}

export default ItemSelect;
