import React, { useEffect } from 'react';
import ItemCard from '../components/ItemCard';
import { connect } from 'react-redux';

import {
  HelpBlock,
  Form,
  FormControl,
  FormGroup,
  IconButton,
  Icon,
  FlexboxGrid,
  Button,
} from 'rsuite';
import { fetchMyOrder } from '../Actions';

const ItemSelect = ({ activeOrder }) => {
  const showing = true;

  useEffect(() => {
    fetchMyOrder();
  }, []);

  console.log(activeOrder);

  const handleClick = () => {
    console.log('click');
  };

  return (
    <FlexboxGrid justify='center'>
      <FlexboxGrid.Item colspan={18}>
        <h1>Returns</h1>
      </FlexboxGrid.Item>
      <FlexboxGrid.Item colspan={18}>
        <h4>Order Number:</h4>
      </FlexboxGrid.Item>
      <FlexboxGrid.Item colspan={20}>
        <Form fluid>
          <FormGroup>
            {activeOrder &&
              activeOrder.map((items) => {
                return <ItemCard items={items} key={items.id} />;
              })}
          </FormGroup>

          <FormGroup>
            <FormControl name='email' placeholder='Email' />
            <HelpBlock tooltip>
              This is where will will send the return label
            </HelpBlock>
          </FormGroup>

          <FormGroup>
            <FormControl name='name' placeholder='Name' />
            <HelpBlock tooltip>This field is required</HelpBlock>
          </FormGroup>

          <FormGroup>
            <IconButton
              onClick={handleClick}
              icon={<Icon icon='arrow-right' />}
              disabled={showing ? false : true}
            >
              Next
            </IconButton>
          </FormGroup>
        </Form>
      </FlexboxGrid.Item>
    </FlexboxGrid>
  );
};
const mapStateToProps = (state) => ({
  activeOrder: state.activeOrder.orderdata,
});

export default connect(mapStateToProps)(ItemSelect);
