import React, { useState } from 'react';
import {
  Panel,
  FlexboxGrid,
  FormControl,
  InputPicker,
  Form,
  Cascader,
} from 'rsuite';
import { reasons } from './reasons';
function ItemCard({ items }) {
  const [returnReason, setReturnReason] = useState();
  console.log(returnReason);
  return (
    <FlexboxGrid>
      <FlexboxGrid.Item>
        <img
          src='https://i.pinimg.com/474x/e6/3d/69/e63d6960e27825cc4a32d5b984de2994.jpg'
          height='140'
          alt='juup'
        />

        <Panel header={items.itemName} style={{ height: 140 }}>
          <p>
            <small>Size: {items.size} </small>
          </p>
          <p>
            <small>Size: </small>
          </p>
        </Panel>
      </FlexboxGrid.Item>
      <Cascader
        onChange={(value) => {
          this.props.callbackChoice(value);
        }}
        data={[
          {
            value: 'returning',
            label: 'return',
            children: [
              {
                label: 'wrong size',
                value: 'wrong size',
              },
              { label: 'wrong color', value: 'wrong colour' },
            ],
          },
        ]}
      />
    </FlexboxGrid>
  );
}

export default ItemCard;
