import axios from 'axios';

export const FetchOrders = () => async (dispatch, getState) => {
  const orders = await axios
    .get('./RefundReasons.JSON')
    .then((res) => res.json());
  dispatch(OrderData(orders));
};

export const fetchMyOrder = async () => {
  const res = await axios.get('./RefundReasons.JSON');
};

export const UserOrderSelection = (userOrderNum) => ({
  type: 'UPDATE_ORDER_NUMBER',
  payload: userOrderNum,
});

export const UpdateReturn = (returningItemsArray) => ({
  type: 'RETURNING_ITEMS',
  payload: returningItemsArray,
});

export const OrderData = (orders) => ({
  type: 'FETCH_ORDER_DATA',
  payload: orders,
});
