import React, { useState, UseState } from 'react';
import ItemCard from '../components/ItemCard';
import { connect } from 'react-redux';
import {
  fetchMyOrder,
  fetch,
  PostReturnData,
  PostReturnReasons,
} from '../Actions';
import { reasons } from '../components/reasons';

import {
  HelpBlock,
  Form,
  FormControl,
  FormGroup,
  IconButton,
  Icon,
  FlexboxGrid,
  Checkbox,
  InputPicker,
  CheckboxGroup,
  Schema,
  Radio,
  RadioGroup,
  Input,
  Button,
} from 'rsuite';
import { useHistory, useParams } from 'react-router';

const ItemSelect = ({
  activeOrder,
  orderNumber,
  postReturn,
  postReasons,
  returnData,
}) => {
  const { order } = useParams();
  const { ArrayType } = Schema.Types;
  const model = Schema.Model({
    ReturnItems: ArrayType(),
  });

  const [returnOrder, setReturnOrder] = useState();
  const [returnitems, setReturnitems] = useState([]);

  const show = activeOrder;

  const history = useHistory();

  const handleConfirm = () => {
    postReasons(returnitems);
  };
  postReturn(returnOrder);

  const handleNext = () => {
    history.push(`/printlabel/:${returnData}`);
  };

  const handleBack = () => {
    history.goBack();
  };

  return (
    <div>
      <FlexboxGrid justify='center'>
        <FlexboxGrid.Item colspan={18}>
          <h1>Returns</h1>
        </FlexboxGrid.Item>
        <FlexboxGrid.Item colspan={18}>
          <h4>Order Number: {orderNumber}</h4>
        </FlexboxGrid.Item>
        <FlexboxGrid.Item colspan={20}>
          <Form
            model={model}
            fluid
            onChange={(formValue) => {
              console.log(formValue);
              setReturnOrder(formValue);
            }}
          >
            <FormGroup name='items'>
              <FlexboxGrid justify='space-around' align='top'>
                <FlexboxGrid.Item>
                  {show &&
                    show.map((items, index) => {
                      return (
                        <div key={items.itemid}>
                          <ItemCard items={items} />
                          <InputPicker
                            data={reasons}
                            onChange={(value) => {
                              let newArr = [...returnitems];
                              newArr[index] = {
                                reason: value,
                                item: items.itemid,
                                itemName: items.itemname,
                              };
                              setReturnitems(newArr);
                            }}
                          />
                        </div>
                      );
                    })}
                </FlexboxGrid.Item>
              </FlexboxGrid>
            </FormGroup>

            <FormGroup name='info'>
              <FormControl name='email' placeholder='Email' />
              <HelpBlock tooltip>
                This is where will will send the return label
              </HelpBlock>
            </FormGroup>

            <FormGroup>
              <FormControl name='name' placeholder='Name' />
              <HelpBlock tooltip>This field is required</HelpBlock>
            </FormGroup>

            <FormGroup>
              <FlexboxGrid justify='space-around'>
                <Button onClick={handleConfirm} style={{ margin: 7 }}>
                  Submit
                </Button>

                {orderNumber ? (
                  <IconButton
                    style={{ margin: 7 }}
                    onClick={handleNext}
                    icon={<Icon icon='arrow-right' />}
                  >
                    Next
                  </IconButton>
                ) : null}
              </FlexboxGrid>
            </FormGroup>
          </Form>
        </FlexboxGrid.Item>
      </FlexboxGrid>
      );
    </div>
  );
};

const mapStateToProps = (state) => ({
  returnData: state.activeReturn.retunNo,
});

const mapDispatchToProps = (dispatch) => {
  return {
    postReturn: (returnOrder) => dispatch(PostReturnData(returnOrder)),
    postReasons: (returnItems) => dispatch(PostReturnReasons(returnItems)),
  };
};

export default connect(mapStateToProps, mapDispatchToProps)(ItemSelect);
