import React, { useEffect } from 'react';
import { Button, Panel, FlexboxGrid } from 'rsuite';
import ItemCard from '../components/ItemCard';
import { connect } from 'react-redux';
import Pdf from 'react-to-pdf';

import Label from '../components/Label';
const LabelPrint = ({ returnNo, user }) => {
  const HandleView = () => {};
  return (
    <FlexboxGrid justify='center' align='bottom' style={{ height: 400 }}>
      <FlexboxGrid.Item colspan={18}>
        <Label returnNo={returnNo} />
        <Panel header={returnNo}>
          You can return items with Royal Mail at over 13,000 locations at Post
          OfficeÂ® branches. No printer required, just visit our returns portal
          to register your return here. Receive your QR code and take it along
          with your parcel to a Post Office branch or Royal Mail Customer
          service point where we can print your label from the code. we have
          sent you a copy of you Retuns lables to the email address previous
          provided download your return label by click the button below<br></br>
          <Button size='lg' style={{ margin: 27 }} onClick={HandleView}>
            view
          </Button>
          <Pdf targetRef={ref} filename='code-example.pdf'>
            {({ toPdf }) => <button onClick={toPdf}>Generate Pdf</button>}
          </Pdf>
        </Panel>
      </FlexboxGrid.Item>
    </FlexboxGrid>
  );
};
const mapStateToProps = (state) => ({
  returnNo: state.returnOrder.returnNo,
  user: state.returnOrder.name,
});

export default connect(mapStateToProps)(LabelPrint);
