import React, { useEffect, useDispatch } from 'react';
import ItemCard from '../components/ItemCard';
import { connect } from 'react-redux';
import { fetchMyOrder, fetch } from '../Actions';

import {
  HelpBlock,
  Form,
  FormControl,
  FormGroup,
  IconButton,
  Icon,
  FlexboxGrid,
  Button,
} from 'rsuite';

const ItemSelect = ({ activeOrder }) => {
  const showing = true;

  const [Returning, setReturing] = useState(false);

  useEffect(() => {
    fetchMyOrder();
  }, []);

  console.log(activeOrder);

  return (
    <FlexboxGrid justify='center'>
      <FlexboxGrid.Item colspan={18}>
        <h1>Returns</h1>
      </FlexboxGrid.Item>
      <FlexboxGrid.Item colspan={18}>
        <h4>Order Number:</h4>
      </FlexboxGrid.Item>
      <FlexboxGrid.Item colspan={20}>
        <Form fluid>
          <FormGroup>
            {activeOrder &&
              activeOrder.map((items) => {
                return (
                  <div>
                    <ItemCard items={items} key={items.id} />
                    <FlexboxGrid.Item colspan={6}>
                      <Checkbox onChange={() => setReturing(!Returning)}>
                        {' '}
                        Return
                      </Checkbox>
                      {!Returning ? null : (
                        <InputPicker
                          data={reasons}
                          placeholder='Select Reason'
                          size='lg'
                        />
                      )}
                    </FlexboxGrid.Item>
                  </div>
                );
              })}
          </FormGroup>

          <FormGroup>
            <FormControl name='email' placeholder='Email' />
            <HelpBlock tooltip>
              This is where will will send the return label
            </HelpBlock>
          </FormGroup>

          <FormGroup>
            <FormControl name='name' placeholder='Name' />
            <HelpBlock tooltip>This field is required</HelpBlock>
          </FormGroup>

          <FormGroup>
            <IconButton
              onClick={fetchMyOrder}
              icon={<Icon icon='arrow-right' />}
              disabled={showing ? false : true}
            >
              Next
            </IconButton>
          </FormGroup>
        </Form>
      </FlexboxGrid.Item>
    </FlexboxGrid>
  );
};
const mapStateToProps = (state) => ({
  activeOrder: state.activeOrder,
});

export default connect(mapStateToProps)(ItemSelect);
