import React, { useState, UseState } from 'react';
import ItemCard from '../components/ItemCard';
import { connect } from 'react-redux';
import { fetchMyOrder, fetch, PostReturnData } from '../Actions';
import { reasons } from '../components/reasons';

import {
  HelpBlock,
  Form,
  FormControl,
  FormGroup,
  IconButton,
  Icon,
  FlexboxGrid,
  Checkbox,
  InputPicker,
  CheckboxGroup,
  Schema,
  Radio,
  RadioGroup,
  InputGroup,
  Input,
} from 'rsuite';
import { useHistory } from 'react-router';

const Newv = ({ activeOrder, orderNumber, postReturn }) => {
  const { ArrayType } = Schema.Types;
  const model = Schema.Model({
    ReturnItems: ArrayType(),
  });

  const [returnOrder, setReturnOrder] = useState();
  const [returnitems, setReturnitems] = useState();

  const show = activeOrder;

  console.log(returnOrder);
  console.log(returnitems);

  const handleSubmit = () => {
    console.log('yoo');
  };

  const history = useHistory();

  const handleNext = () => {
    postReturn(returnOrder);

    history.push('./printlabel');
  };

  const handleBack = () => {
    history.goBack();
  };

  return (
    <div>
      <FlexboxGrid justify='center'>
        <FlexboxGrid.Item colspan={18}>
          <h1>Returns</h1>
        </FlexboxGrid.Item>
        <FlexboxGrid.Item colspan={18}>
          <h4>Order Number: {orderNumber}</h4>
        </FlexboxGrid.Item>

        <FlexboxGrid.Item colspan={20}>
          <InputGroup>
            <InputGroup.Addon>
              <Icon icon='avatar' />
            </InputGroup.Addon>
            <Input
              onChange={(value) => {
                console.log(value);
              }}
            />
          </InputGroup>
        </FlexboxGrid.Item>

        <CheckboxGroup
          onChange={(value) => {
            console.log(value);
          }}
          name='ReturnItems'
          inline
        >
          {show &&
            show.map((items) => {
              return (
                <div key={items.size}>
                  <Checkbox value={items}>return</Checkbox>
                  <ItemCard items={items} />
                </div>
              );
            })}
        </CheckboxGroup>

        <FlexboxGrid.Item colspan={20}>
          <InputGroup>
            <InputGroup.Addon>
              <Icon icon='avatar' />
            </InputGroup.Addon>
            <Input
              onChange={(value) => {
                console.log(value);
              }}
            />
          </InputGroup>
        </FlexboxGrid.Item>
      </FlexboxGrid>
    </div>
  );
};

const mapStateToProps = (state) => ({
  activeOrder: state.activeOrder.basket,
  orderNumber: state.activeOrder.userOrder,
});

const mapDispatchToProps = (dispatch) => {
  return {
    postReturn: (returnItems) => dispatch(PostReturnData(returnItems)),
  };
};

export default connect(mapStateToProps, mapDispatchToProps)(Newv);
