import axios from 'axios';
import { OrderData } from '../Actions';
import { FETCH_ORDER_DATA } from '../Actions/types';

const initialState = {
  orderdata: [
    {
      image: 'Shocks',
      size: 7,
      id: 30494,
    },
  ],
};

export const userOrder = (state = initialState, { type, payload }) => {
  switch (type) {
    case FETCH_ORDER_DATA:
      return { ...state, orderdata: payload };

    default:
      return state;
  }
};

export const FetchOrders = () => async (dispatch, getState) => {
  const orders = await axios
    .get('./RefundReasons.JSON')
    .then((res) => res.json());
  dispatch(OrderData(orders));
};
