import React, { useState } from 'react';
import { Panel, FlexboxGrid, FormControl, InputPicker, Form } from 'rsuite';
import { reasons } from './reasons';
function ItemCard({ items }) {
  const [returnReason, setReturnReason] = useState();
  return (
    <FlexboxGrid>
      <FlexboxGrid.Item>
        <img
          src='https://i.pinimg.com/474x/e6/3d/69/e63d6960e27825cc4a32d5b984de2994.jpg'
          height='140'
          alt='juup'
        />

        <Panel header={items.itemName} style={{ height: 140 }}>
          <p>
            <small>Size: {items.size} </small>
          </p>
          <p>
            <small>Size: </small>
          </p>
        </Panel>
      </FlexboxGrid.Item>

      <FormControl
        accepter={InputPicker}
        data={reasons}
        placeholder='Select Reason'
        size='md'
        onChange={(value) => setReturnReason(value)}
        name={items.itemName + ' ' + returnReason}
      />
    </FlexboxGrid>
  );
}

export default ItemCard;
