import axios from 'axios';
import { useHistory } from 'react-router-dom';

export const fetchMyOrder = () => {
  return async (dispatch) => {
    console.log('update-start');
    const ress = await axios.get('http://localhost:3000/all');
    console.log(ress.data);

    dispatch(OrderData(ress.data.basket));
  };
};

export const FetchOrderData = (orders) => {
  return async (dispatch) => {
    console.log(orders);
    dispatch({
      type: 'FIND_MY_ORDER',
    });
    const ress = await axios.post('http://localhost:3000/fetchOrder', {
      orderNum: orders,
    });
    console.log(ress.data);

    if (ress.data !== 'not found') {
      dispatch(OrderData(ress.data));
    } else {
      console.log('waaaiit');
      dispatch({
        type: 'NOT_FOUND',
      });
    }
  };
};

export const fetch = async () => {
  await axios.get('./RefundReasons.JSON').then((res) => console.log(res.data));
};

export const UserOrderSelection = (userOrderNum) => ({
  type: 'UPDATE_ORDER_NUMBER',
  payload: userOrderNum,
});

export const UpdateReturn = (returningItemsArray) => ({
  type: 'RETURNING_ITEMS',
  payload: returningItemsArray,
});

export const OrderData = (orders) => ({
  type: 'MY_ORDER',
  payload: orders,
});
