import React, { useState } from 'react';
import { Panel, FlexboxGrid, Checkbox, InputPicker } from 'rsuite';

const data = [
  {
    label: 'Wrong size',
    value: 'Eugenia',
  },
  {
    label: 'Wrong colour',
    value: 'Wrong Colour',
  },
  {
    label: 'Other',
    value: 'Other',
  },
  {
    label: 'Did not suit',
    value: 'Did not suit',
  },
];

function ItemCard({ items }) {
  const [Returning, setReturing] = useState(false);

  console.log(items.size);

  return (
    <FlexboxGrid justify='space-around' align='middle' style={{ margin: 10 }}>
      <FlexboxGrid.Item colspan={6}>
        <img
          src='https://i.pinimg.com/474x/e6/3d/69/e63d6960e27825cc4a32d5b984de2994.jpg'
          height='140'
          alt='juup'
        />

        <Panel header={items.image} style={{ height: 140 }}>
          <p>
            <small>Size: {items.size} </small>
          </p>
          <p>
            <small>Size: </small>
          </p>
        </Panel>
      </FlexboxGrid.Item>
      <FlexboxGrid.Item colspan={6}>
        <Checkbox onChange={() => setReturing(!Returning)}> Return</Checkbox>
        {!Returning ? null : (
          <InputPicker data={data} placeholder='Select Reason' size='lg' />
        )}
      </FlexboxGrid.Item>
    </FlexboxGrid>
  );
}

export default ItemCard;
