import { NEW_RETURN, RETURNING_ITEMS } from '../Actions/types';

const initialState = {
  returnNo: null,
  name: null,
  isLoading: false,
  items: [],
};

const activeReturn = (state = initialState, { type, payload }) => {
  switch (type) {
    case NEW_RETURN:
      return { ...state, returnNo: null, name: null, isLoading: false };
    case RETURNING_ITEMS:
      return {
        ...state,
        returnNo: payload.returnNo,
        name: payload.name,
        isLoading: false,
      };
    default:
      return state;
  }
};

export default activeReturn;
