import React, { useState } from 'react';
import {
  Input,
  Whisper,
  Tooltip,
  Button,
  HelpBlock,
  FormControl,
  ControlLabel,
  FormGroup,
  FlexboxGrid,
  Form,
  Panel,
} from 'rsuite';

import 'rsuite/dist/styles/rsuite-default.css';

const Returns = () => {
  const [isLoading, setLoading] = useState(false);

  const handleSubmit = (userOrderNumer) => {
    setLoading(true);
    console.log(userOrderNumer);
  };

  return (
    <div>
      <FlexboxGrid justify='center' align='middle' style={{ height: 600 }}>
        <FlexboxGrid.Item colspan={18}>
          <Panel header={<h2>returns</h2>} bordered>
            <Form
              onChange={(formValue) => {
                console / log(formValue);
              }}
              fluid
            >
              <FormGroup>
                <ControlLabel>Order number</ControlLabel>
                <FormControl name='orderNumber' />
                <HelpBlock tooltip>Required</HelpBlock>
              </FormGroup>

              <Button onClick={handleSubmit} loading={isLoading ? true : false}>
                Submit
              </Button>
            </Form>
          </Panel>
        </FlexboxGrid.Item>
      </FlexboxGrid>
    </div>
  );
};

<Whisper trigger='focus' speaker={<Tooltip>Enter your order number</Tooltip>}>
  <Input style={{ width: 300 }} placeholder='AA00000A0000' size='lg' />
</Whisper>;

export default Returns;
