import React, { useEffect, useState } from 'react';
import ItemCard from '../components/ItemCard';
import { connect } from 'react-redux';
import { fetchMyOrder, fetch } from '../Actions';
import { reasons } from '../components/reasons';

import {
  HelpBlock,
  Form,
  FormControl,
  FormGroup,
  IconButton,
  Icon,
  FlexboxGrid,
  Checkbox,
  InputPicker,
  CheckboxGroup,
  Schema,
} from 'rsuite';

const { ArrayType, StringType, NumberType } = Schema.Types;
const model = Schema.Model({
  ReturnItems: ArrayType(),
});

const ItemSelect = ({ activeOrder }) => {
  const showing = true;

  const [Returning, setReturing] = useState(false);
  const [returnBasket, setReturnBasket] = useState([]);

  const formValue = {
    email: '',
    name: '',
    returnId: 'Chrome',
    selectReason: ['open'],
  };

  useEffect(() => {
    fetchMyOrder();
  }, []);

  const handleSubmit = () => {
    console.log('yoo');
  };

  const handleSw = (value) => {
    console.log(value);
  };

  console.log(setReturnBasket);

  return (
    <FlexboxGrid justify='center'>
      <FlexboxGrid.Item colspan={18}>
        <h1>Returns</h1>
      </FlexboxGrid.Item>
      <FlexboxGrid.Item colspan={18}>
        <h4>Order Number:</h4>
      </FlexboxGrid.Item>
      <FlexboxGrid.Item colspan={20}>
        <Form
          model={model}
          fluid
          onChange={(formValue) => {
            console.log({ formValue });
          }}
        >
          <FormGroup name='items'>
            <FlexboxGrid justify='space-around' align='bottom'>
              <FlexboxGrid.Item>
                {activeOrder &&
                  activeOrder.map((items) => {
                    return (
                      <div>
                        <ItemCard items={items} key={items.id} />
                        <FormControl
                          accepter={Checkbox}
                          name='ReturnItems'
                          value={items.size}
                        ></FormControl>
                      </div>
                    );
                  })}

                {!Returning ? null : (
                  <FormControl
                    accepter={InputPicker}
                    data={reasons}
                    placeholder='Select Reason'
                    size='md'
                    onChange={(value) => console.log(value)}
                    name='selectReason'
                  />
                )}
              </FlexboxGrid.Item>
            </FlexboxGrid>
          </FormGroup>

          <FormGroup name='info'>
            <FormControl name='email' placeholder='Email' />
            <HelpBlock tooltip>
              This is where will will send the return label
            </HelpBlock>
          </FormGroup>

          <FormGroup>
            <FormControl name='name' placeholder='Name' />
            <HelpBlock tooltip>This field is required</HelpBlock>
          </FormGroup>

          <FormGroup>
            <IconButton
              onClick={handleSubmit}
              icon={<Icon icon='arrow-right' />}
              disabled={showing ? false : true}
            >
              Next
            </IconButton>
          </FormGroup>
        </Form>
      </FlexboxGrid.Item>
    </FlexboxGrid>
  );
};
const mapStateToProps = (state) => ({
  activeOrder: state.activeOrder,
});

export default connect(mapStateToProps)(ItemSelect);
