import React, { Component } from 'react';
import ItemCard from '../components/ItemCard';
import { connect } from 'react-redux';
import { fetchMyOrder, fetch } from '../Actions';
import { reasons } from '../components/reasons';

import {
  HelpBlock,
  Form,
  FormControl,
  FormGroup,
  IconButton,
  Icon,
  FlexboxGrid,
  Checkbox,
  InputPicker,
  CheckboxGroup,
  Schema,
} from 'rsuite';

class ItemSelect extends Component {
  componentDidMount() {
    fetch();
  }
  render() {
    const { activeOrder } = this.props;
    const { ArrayType } = Schema.Types;
    const model = Schema.Model({
      ReturnItems: ArrayType(),
    });

    const showing = true;

    const handleSubmit = () => {
      console.log('yoo');
    };

    const handleSw = (value) => {
      console.log(value);
    };

    return (
      <div>
        <FlexboxGrid justify='center'>
          <FlexboxGrid.Item colspan={18}>
            <h1>Returns</h1>
          </FlexboxGrid.Item>
          <FlexboxGrid.Item colspan={18}>
            <h4>Order Number:</h4>
          </FlexboxGrid.Item>
          <FlexboxGrid.Item colspan={20}>
            <Form
              model={model}
              fluid
              onChange={(formValue) => {
                console.log({ formValue });
              }}
            >
              <FormGroup name='items'>
                <FlexboxGrid justify='space-around' align='bottom'>
                  <FlexboxGrid.Item>
                    <FormControl accepter={CheckboxGroup} name='ReturnItems'>
                      {activeOrder.map((items) => {
                        return (
                          <div key={items.size}>
                            <Checkbox
                              value={{
                                id: items.name,
                                reason: null,
                              }}
                            >
                              Return
                            </Checkbox>
                            <ItemCard items={items} />
                          </div>
                        );
                      })}
                    </FormControl>
                  </FlexboxGrid.Item>
                </FlexboxGrid>
              </FormGroup>

              <FormGroup name='info'>
                <FormControl name='email' placeholder='Email' />
                <HelpBlock tooltip>
                  This is where will will send the return label
                </HelpBlock>
              </FormGroup>

              <FormGroup>
                <FormControl name='name' placeholder='Name' />
                <HelpBlock tooltip>This field is required</HelpBlock>
              </FormGroup>

              <FormGroup>
                <IconButton
                  onClick={handleSubmit}
                  icon={<Icon icon='arrow-right' />}
                  disabled={showing ? false : true}
                >
                  Next
                </IconButton>
              </FormGroup>
            </Form>
          </FlexboxGrid.Item>
        </FlexboxGrid>
        );
      </div>
    );
  }
}
const mapStateToProps = (state) => ({
  activeOrder: state.activeOrder,
});

export default connect(mapStateToProps)(ItemSelect);
